/*global require, console, module */
var prompt = require('prompt'),
    resources = require('./resources/resources'),
    wwwLib = require('./www'),
    sqeLib = require('./sqe'),
    fs = require('fs');

function cli(argv) {
    'use strict';

    // Get variables
    var resource = resources('en_EN'),
        sqe = sqeLib(),
        www = wwwLib();

    // Console output
    console.log(resource.CLI_INTRODUCTION);

    switch (argv[2]) {
    case "--serve":
        www.start();
        break;
    case "init":
        console.log('');
        prompt.message = "".green;
        prompt.delimiter = ">>".green;
        prompt.start();
        prompt.get([{
            name: 'database',
            description: resource.CLI_DATABASE_NAME,
            required: true
        }, {
            name: 'config',
            description: resource.CLI_CONFIG_FILENAME,
            required: true
        }], function (err, result) {
            // 
            // Log the results. 
            // 
            console.log();
            console.log(resource.CLI_OUTPUT_INTRODUCTION);
            console.log(resource.CLI_DATABASE + result.database);
            console.log(resource.CLI_CONFIG + result.config);

            fs.readFile(result.config, 'utf8', function (err, data) {
                var json = JSON.parse(data);
                if (err) {
                    return console.log(err);
                }
                
                // Async create SQL database from JSON input.
                sqe.open('./' + result.database);
                sqe.createTable(json, function (table) {
                    console.log(resource.CLI_CREATED_TABLE + table + resource.CLI_IN_DATABASE + result.database);
                    sqe.showTable(json, function() {
                        sqe.close();
                    });
                });
                
            });

        });
        break;
    }

}
module.exports = cli;