/*global module */
function resources(lang) {
    'use strict';

    var res = {
        'en_EN' : {
            /* CLI resource strings */
            'CLI_INTRODUCTION': 'Mendix SQLite Engine - CLI',
            'CLI_DATABASE_NAME': ' Name of the database ',
            'CLI_CONFIG_FILENAME': ' The config filename to read ',
            'CLI_OUTPUT_FILENAME': ' The path to the output file ',
            'CLI_OUTPUT_INTRODUCTION': 'Command-line input received:',
            'CLI_DATABASE': '  database: ',
            'CLI_CONFIG': '  configuration file: ',
            'CLI_OUTPUT': '  output: ',
            'CLI_GENERATED_SQL': 'Generated SQL statements',
            'CLI_SHOW_TABLEINFORMATION': 'Show table information from table: ',
            'CLI_CREATED_TABLE': 'Created table: ',
            'CLI_IN_DATABASE': ' in database '
        }
    };

    return res[lang];

}

module.exports = resources;