/*global require, console, module */
var resources = require('./resources/resources'),
    sqlite3 = require('sqlite3').verbose();

function sqe() {
    'use strict';
    var link = null,
        resource = resources('en_EN');
    return {
        link: link,
        open: function (filename, type, callback) {
            this.link = new sqlite3.Database(filename);
        },
        close: function () {
            if (this.link) {
                this.link.close();
            }
        },
        createTable: function (json, callback) {
            var sql = '', action = null, field = null, fieldContent = null;
            sql += 'CREATE TABLE IF NOT EXISTS ' + json.name + '(id INT PRIMARY KEY);\n';
            for (field in json.fields) {
                if (json.fields.hasOwnProperty(field)) {
                    fieldContent = json.fields[field];
                    sql += 'ALTER TABLE ' + json.name + ' ADD COLUMN ' + fieldContent.name + ' ' + fieldContent.ftype + ';\n';
                }
            }
            console.log();
            console.log(resource.CLI_GENERATED_SQL);
            console.log(sql);
            this.link.exec(sql, function (link) {
                callback(json.name);
            });
        },
        showTable: function (json, callback) {
            if (this.link) {
                console.log('');
                console.log(resource.CLI_TABLE_INFORMATION + json.name);
                this.link.each("PRAGMA table_info('" + json.name + "')", function (err, row) {
                    console.log(row.name + ": " + row.type + ((row.pk) ? ' IS PRIMARY KEY' : ''));
                });
                callback();
            }
        }
    };
}

module.exports = sqe;